                    type=example.challenges.apply(lambda challenges: challenges.apply(lambda challenges: challenges[range["value"]].dns_type)),
                    datas=[example.challenges.apply(lambda challenges: challenges.apply(lambda challenges: challenges[range["value"]].dns_value))],
