from dataclasses import dataclass
import enum


def _flag_to_iso(flag: str) -> str:
    return "".join(
        chr(ord(c) - 0x1F1E6 + ord("A"))
        for c in flag
    )


def _iso_to_flag(iso: str) -> str:
    return "".join(
        chr(ord("ðŸ‡¦") + ord(c) - ord("A"))
        for c in iso.upper()
    )


@dataclass(frozen=True, order=True)
class _Country:
    id: int
    iso: str
    label: str

    @property
    def flag(self) -> str:
        return _iso_to_flag(self.iso)

    def __str__(self) -> str:
        return self.label


